cve38352_version_lt(){
    awk -v v1="$1" -v v2="$2" '
    function cleannum(val) {
        gsub(/[^0-9].*/, "", val)
        if (val == "") {
            val = 0
        }
        return val + 0
    }
    BEGIN {
        n = split(v1, a, ".")
        m = split(v2, b, ".")
        max = (n > m ? n : m)
        for (i = 1; i <= max; i++) {
            av = (i <= n ? cleannum(a[i]) : 0)
            bv = (i <= m ? cleannum(b[i]) : 0)
            if (av < bv) {
                exit 0
            }
            if (av > bv) {
                exit 1
            }
        }
        exit 1
    }'
}
cve38352_sanitize_version(){
    printf "%s" "$1" | tr '-' '.' | sed 's/[^0-9.].*$//' | sed 's/\.\./\./g' | sed 's/^\.//' | sed 's/\.$//'
}
