/*
** Record the fact that we want to lock a table at run-time.
**
** The table to be locked has root page iTab and is found in database iDb.
** A read or a write lock can be taken depending on isWritelock.
**
** This routine just records the fact that the lock is desired.  The
** code to make the lock occur is generated by a later call to
** codeTableLocks() which occurs during sqlite3FinishCoding().
*/
static void sqlite3TableLock(
Parse pParse,     /* Parsing context */
int iDb,          /* Index of the database containing the table to lock */
int iTab,         /* Root page number of the table to be locked */
u8 isWriteLock,   /* True for a write lock */
string zName      /* Name of the table to be locked */
)
{
int i;
int nBytes;
TableLock p;

Debug.Assert( iDb >= 0 );

for ( i = 0 ; i < pParse.nTableLock ; i++ )
{
p = pParse.aTableLock[i];
if ( p.iDb == iDb && p.iTab == iTab )
{
p.isWriteLock = (byte)( ( p.isWriteLock != 0 || isWriteLock != 0 ) ? 1 : 0 );
return;
}
}

nBytes = ( pParse.nTableLock + 1 );//sizeof(TableLock) *
Array.Resize( ref pParse.aTableLock, pParse.nTableLock + 1 );
//          sqlite3DbReallocOrFree( pParse.db, pParse.aTableLock, nBytes );
if ( pParse.aTableLock != null )
{
pParse.aTableLock[pParse.nTableLock] = new TableLock();
p = pParse.aTableLock[pParse.nTableLock++];
p.iDb = iDb;
p.iTab = iTab;
p.isWriteLock = isWriteLock;
p.zName = zName;
}
else
{
pParse.nTableLock = 0;
pParse.db.mallocFailed = 1;
}
}

/*
** Code an OP_TableLock instruction for each table locked by the
** statement (configured by calls to sqlite3TableLock()).
*/
static void codeTableLocks( Parse pParse )
{
int i;
Vdbe pVdbe;

pVdbe = sqlite3GetVdbe( pParse );
Debug.Assert( pVdbe != null ); /* sqlite3GetVdbe cannot fail: VDBE already allocated */

for ( i = 0 ; i < pParse.nTableLock ; i++ )
{
TableLock p = pParse.aTableLock[i];
int p1 = p.iDb;
sqlite3VdbeAddOp4( pVdbe, OP_TableLock, p1, p.iTab, p.isWriteLock,
p.zName, P4_STATIC );
}
}
